    "mark_zuckerberg": {
      "role": "Chief Executive Officer",
      "responses": [
        {
          "topic": "Five AI-driven opportunities",
          "content": "AI is transforming ads, engagement, business messaging, Meta AI, and AI devices; we are investing in each downstream of our general intelligence and infrastructure builds."
        },
        {
          "topic": "Meta AI scale and personalization",
          "content": "Almost 1B monthly actives use Meta AI; we are deepening personalization, voice, entertainment, and building the standalone Meta AI app while integrating it deeply across WhatsApp, Facebook, Instagram, and Threads."
        },
        {
          "topic": "AI devices and glasses",
          "content": "Ray-Ban Meta AI glasses have tripled sales year over year, voice usage is accelerating, live translations are rolling out globally, and Quest 3S/Vision tools continue to expand Horizon experiences."
        },
        {
          "topic": "Llama roadmap",
          "content": "We released the first Llama 4 models and are building Behemoth; longer context windows and low-latency models let us distill intelligence into efficient, personalized services for Meta AI and monetization."
        }
      ]
    },
    "susan_li": {
      "role": "Chief Financial Officer",
      "responses": [
        {
          "topic": "Q1 results",
          "content": "Revenue $42.3B (+16%, +19% constant currency), operating income $17.6B (41% margin), net income $16.6B, EPS $6.43, free cash flow $10.3B; share repurchases $13.4B, dividends $1.3B, ending cash ~$70.2B."
        },
        {
          "topic": "Family of Apps performance",
          "content": "Family revenue $41.9B (+16%), ads $41.4B (+16% / +20% cc); DAP grew to 3.4B, impressions +5%, average price per ad +10%, business messaging and Advantage+ Creative momentum, Threads monetization and ads ramping gradually."
        },
        {
          "topic": "Reality Labs traction",
          "content": "Reality Labs revenue $412M (-6%) while expenses grew 8% to $4.6B; Ray-Ban Meta AI glasses MAUs and voice activity accelerating, live translations launching, and VR engagement improving with Quest 3S."
        },
        {
          "topic": "CapEx and infrastructure outlook",
          "content": "CapEx $13.7B; full-year guide raised to $64-72B to fund data center builds and higher hardware costs while keeping most spend directed to core business and flexible deployment."
        },
        {
          "topic": "Expense and tax guidance",
          "content": "Full-year expense guide trimmed to $113-118B, balancing lower personnel and non-headcount forecasts against higher infrastructure/Reality Labs COGS; full-year tax rate still 12-15%."
        }
      ]
    }
  },
